import React from 'react'

const Charactergrid = ({items, isLoading}) => {
    return isLoading ? (<h1>Loading....</h1>) : (
        (
            <section className='cards'>
            {items.map(item => (
                <h1 key={item.id}>
                  <h1 > {item.name}</h1>
                <br></br>
                <a href='./'><img src={item.thumbnail.path + "/portrait_xlarge.jpg"} alt=''/> </a>

                </h1>

                ))}
                </section>

        ) 
    )
}

export default Charactergrid
