import Header from './components/ui/Header';
import axios from 'axios'
import { useEffect, useState } from 'react';

const hash = "01ba5ca5394b332fb8163313e0909d11"
const App = () => {
  const [items, setItems] = useState([])
  const [loading, isLoading] = useState(true)



  useEffect(() => {
    const fetchItems = async () => {
      // const result = await axios (
      // `https://gateway.marvel.com/v1/public/characters?ts=1&apikey=b75b3e36882f682bbfd481f1eac4c181&hash=${hash}`
      // )

      const result = await axios(
        `http://gateway.marvel.com/v1/public/characters?series=avengers&limit=40&apikey=b75b3e36882f682bbfd481f1eac4c181&hash=${hash}`

        // `http://gateway.marvel.com/v1/public/characters?ts=1&apikey=b75b3e36882f682bbfd481f1eac4c181&hash=${hash}`
        )
      console.log(result.data.data.results)
      setItems(result.data.data.results)
      isLoading(false)

    }
    fetchItems();
  }, [])
  return (
    <div>
      <div className="container">
      <Header />

      {items.map(item => (
        <section key={item.id}>
          <h1 > {item.name}</h1>
        <br></br>
        <img src={item.thumbnail.path + "/portrait_xlarge.jpg"} />
        </section>

        ))}

    </div>
    </div>
  )
}

export default App
